import './singup.css'
import React , {useState} from 'react'
import {useNavigate} from 'react-router-dom'
import axios from '../../utils/axios'




 const SingUp = () => {
     const [username ,setUsername] = useState(null);
        con
      
     const [password , setPassword] = useState(null);
     
     const [email, setEmail] = useState(null);
     const [fullName , setFullname] = useState(null);
     const [phoneNumber , setPhonenumber] = useState(null)
     const [error, setError] = useState(false);

     const navigate = useNavigate()

     const handlerSingup =async () => {
        if(!username || !password || !email || !phoneNumber ||!fullName) {
            setError(true)
        }else {
            setError(false)
        }

         const user = {
            username : username,
            password : password,
            email : email,
            fullName : fullName,
            phoneNumber : phoneNumber
         }
         try{
            const response = await axios.post("/sign-up", user);

            if (response.status === 201) {
                // Tạo tài khoản thành công
                // Lưu trữ thông tin người dùng vào local storage
                localStorage.setItem("user", response.data);

                // Chuyển hướng người dùng về trang Login
                navigate("/login");
            } else {
                // Tạo tài khoản thất bại
                setError(true);
            }
         }catch(err) {
            setError(true)
         }
         
     }



     return (
        <div>
            <div className='main'>
                <div className='container'>
                    <div className='item'>
                        <h1>Sing Up</h1>
                    </div>

                    <div className='item'>
                        <input type='text' placeholder='UserName' className='input' onChange={(e)=> setUsername(e.target.value)} />
                    </div>

                    <div className='item'>
                        <input type='password' placeholder='Password' className='input' onChange={(e)=> setPassword(e.target.value)} />
                    </div>
                   

                    <div className='item'>
                        <input type='text' placeholder='Full Name' className='input' onChange={(e)=> setFullname(e.target.value)} />
                    </div>

                    <div className='item'>
                        <input type='text' placeholder='Number Phone' className='input' onChange={(e)=> setPhonenumber(e.target.value)} />
                    </div>

                    <div className='item'>
                        <input type='email' placeholder='Email' className='input' onChange={(e)=> setEmail(e.target.value)} />
                    </div>

                    <div className='item'>
                        <button  className='button'  onClick={handlerSingup}>Create Account</button>
                    </div>

                    <div className='item'>
                        {error && <span className='error'>Invalid Credentials!</span>}
                    </div>
                </div>
            </div>
        </div>
     )

 }

 export default SingUp
