import "./login.css";
import React, { useState } from "react";
import axios from "../../utils/axios";
import { useNavigate } from "react-router-dom";

const Login = ({ setUser }) => {
  const [username, setUsername] = useState(null);
  const [password, setPassword] = useState(null);
  const [error, setError] = useState(false);

  //sử lý Login
  const handlerLogin = async () => {
    const user = {
      username: username,
      password: password,
    };
    try {
      await axios
        .post("/login", user)
        .then((res) => {
          localStorage.setItem("user", JSON.stringify(res.data));
          setUser(re)
        })
        .catch((error) => {
          console.log(error);
        });
    } catch (err) {
      setError(true);
    }
  };

  return (
    <div>
      <div className="main">
        <div className="container">
          <div className="item">
            <h1>Login</h1>
          </div>

          <div className="item">
            <input
              type="text"
              placeholder="Username"
              className="input"
              onChange={(e) => setUsername(e.target.value)}
            />
          </div>

          <div className="item">
            <input
              type="password"
              placeholder="Password"
              className="input"
              onChange={(e) => setUsername(e.target.value)}
            />
          </div>

          <div className="item">
            <button className="button" onClick={handlerLogin}>
              Login
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
